import React from "react"
import "./Background.css"
import video from "../../assets/164564_Crops growing in a field aerial _By_Delta_Vision_Artlist_HD.mp4"
import image1 from "../../assets/sunny-hops-field-at-evening.jpg"
import image2 from "../../assets/pexels-skyriusmarketing-2129819.jpg"
import image3 from "../../assets/mint-green-leaves-pattern-background(1).jpg"
 const Background = ({ playStatus, heroStatus }) => {
console.log('Background component:',{playStatus,heroStatus})

  if (playStatus === true) {
    return (

      <video className="background" autoPlay loop muted>
        <source src={ video} type="video mp4"/>
      </video>
    )
  }
  else if (heroStatus === 0) {
    return <img src={ image1} className="background" alt=""/>
  }
  else if (heroStatus === 1) {
    return <img src={ image2} className="background" alt=""/>
  }
  else if (heroStatus === 2) {
    return <img src={ image3} className="background" alt=""/>
  } else {
    return null;
  }

 }
export default Background;
