import React, { useState } from 'react';
import Background from './Components/Background/Background.jsx';
import Navbar from './Components/Navbar/Navbar.jsx';
import Hero from './Components/Hero/Hero.jsx';
import { Route, Routes } from 'react-router-dom';

const App = () => {
  let heroData = [
    { text1: "Explore a world of", text2: "agriculture with my application" },
    { text1: "Discover new farming techniques", text2: "and practices that you'll be using" },
    { text1: "Stay up to date", text2: "on the latest agricultural news and trends" }
  ];

  const [heroCount, setHeroCount] = useState(0);
  const [playStatus, setPlayStatus] = useState(false);

  return (
    <div className="App">
      <Navbar />
      <Routes>
        <Route path="/" element={<Background playStatus={playStatus} heroStatus={heroCount} />} />
        <Route path="/hero" element={
          <Hero
            heroData={heroData}
            setHeroCount={setHeroCount}
            heroCount={heroCount}
            playStatus={playStatus}
            setPlayStatus={setPlayStatus}
          />
        } />
      </Routes>
    </div>
  );
};

export default App;
