import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Background from './Components/Background/Background';
import Navbar from './Components/Navbar/Navbar';
import Hero from './Components/Hero/Hero';
import Login from './Pages/Login/Login';

const App = () => {
  const heroData = [
    { text1: "Explore a world of", text2: "agriculture with my application" },
    { text1: "Discover new farming techniques", text2: "and practices that you'll be using" },
    { text1: "Stay up to date", text2: "on the latest agricultural news and trends" }
  ];

  const [heroCount, setHeroCount] = useState(0);
  const [playStatus, setPlayStatus] = useState(false);

  console.log('App component:', { playStatus, heroCount });

  useEffect(() => {
    const intervalId = setInterval(() => {
      setHeroCount((count) => (count === 2 ? 0 : count + 1));
    }, 5000);


    return () => clearInterval(intervalId);
  }, []);

  return (

    <Router>
      <Navbar />
        <Background playStatus={playStatus} heroStatus={heroCount} />
      <Routes>
        <Route path="/login" element={<Login />} />
      </Routes>
      <Hero
        heroData={heroData}
        setHeroCount={setHeroCount}
        heroCount={heroCount}
        playStatus={playStatus}
        setPlayStatus={setPlayStatus}
      />
    </Router>

  );
};

export default App;
